void rat_template::copy_value(const rat& other_value)
{
if (!other_value.is_bound()) TTCN_error("Initialization of a template of type @JsonTypeAny.rat with an unbound value.");
single_value.n_elements = other_value.size_of();
single_value.value_elements = (anytype_template**)allocate_pointers(single_value.n_elements);
for (int elem_count = 0; elem_count < single_value.n_elements; elem_count++) {
if (other_value[elem_count].is_bound()) {
single_value.value_elements[elem_count] = new anytype_template(other_value[elem_count]);
} else {
single_value.value_elements[elem_count] = new anytype_template;
}
}
set_selection(SPECIFIC_VALUE);
}

void rat_template::copy_template(const rat_template& other_value)
{
switch (other_value.template_selection) {
case SPECIFIC_VALUE:
single_value.n_elements = other_value.single_value.n_elements;
single_value.value_elements = (anytype_template**)allocate_pointers(single_value.n_elements);
for (int elem_count = 0; elem_count < single_value.n_elements; elem_count++) {
if (UNINITIALIZED_TEMPLATE != other_value.single_value.value_elements[elem_count]->get_selection()) {
single_value.value_elements[elem_count] = new anytype_template(*other_value.single_value.value_elements[elem_count]);
} else {
single_value.value_elements[elem_count] = new anytype_template;
}
}
case OMIT_VALUE:
case ANY_VALUE:
case ANY_OR_OMIT:
break;
case VALUE_LIST:
case COMPLEMENTED_LIST:
value_list.n_values = other_value.value_list.n_values;
value_list.list_value = new rat_template[value_list.n_values];
for (unsigned int list_count = 0; list_count < value_list.n_values; list_count++)
value_list.list_value[list_count].copy_template(other_value.value_list.list_value[list_count]);
break;
default:
TTCN_error("Copying an uninitialized/unsupported template of type @JsonTypeAny.rat.");
break;
}
set_selection(other_value);
}

