void anytype::set_param(Module_Param& param)
{
  param.basic_check(Module_Param::BC_VALUE, "union value");
  Module_Param_Ptr m_p = &param;
  if (m_p->get_type()==Module_Param::MP_Value_List && m_p->get_size()==0) return;
  if (m_p->get_type()!=Module_Param::MP_Assignment_List) {
    param.error("union value with field name was expected");
  }
  Module_Param* mp_last = m_p->get_elem(m_p->get_size()-1);
  char* last_name = mp_last->get_id()->get_name();
  if (!strcmp(last_name, "integer")) {
    AT_INTEGER().set_param(*mp_last);
    if (!AT_INTEGER().is_bound()) clean_up();
    return;
  }
  if (!strcmp(last_name, "float")) {
    AT_FLOAT().set_param(*mp_last);
    if (!AT_FLOAT().is_bound()) clean_up();
    return;
  }
  mp_last->error("Field %s does not exist in type @JsonTypeAny.anytype.", last_name);
}

